import React, { createContext, useState } from 'react'

export const FullNameContext = createContext()
console.log(FullNameContext);
export default function ContextAPI() {
    const [fullName, setFullName] = useState()
    const vu = "Vu"
    const handleChange = (e) => {
        setFullName(e.target.value)
        console.log(fullName);
    }
    return (
        <FullNameContext.Provider value={vu}>
            <div>
                <h1>Context API</h1>
                <div className="input-group mb-3">
                    <button className="btn btn-outline-secondary" type="button" id="button-addon1">Button</button>
                    <input className="form-control" onChange={(e) => { handleChange(e) }} />
                </div>
                {
                    fullName && <p className='text-center'>{fullName}</p>
                }
            </div>
        </FullNameContext.Provider>
    )
}
