import React, { useState } from 'react'

export default function Upload_Multiple() {
    const [images, setImages] = useState([])
    const handleChange = (e) => {
        const newImages = [...images];
        for (let i = 0; i < e.target.files.length; i++) {
            newImages.push(URL.createObjectURL(e.target.files[i]));
        }
        setImages(newImages);
    }

    const handleRemove = (index) => {
        // const listImages = [images]
        console.log(index);
    }
    return (
        <div>
            Upload_Multiple
            <input type='file' multiple name='image' onChange={handleChange} />
            {
                images.map((item, index) => (
                    <>
                        <button key={index} type="button" className="btn-close" onClick={handleRemove({ index })}></button>
                        <img className='m-3' key={index} src={item} />
                    </>
                ))
            }
        </div>
    )
}
