import React, { useEffect, useState } from 'react'
import { getAllUserAccount } from '../API/ApiService'

export default function AddProduct() {
    const [previewImage, setPreviewImage] = useState("https://cdn.vortexs.io/api/images/7E39FDE2-2BCB-48D2-AFAC-08F8BFA48A7A/1920/w/tui-life-work-metal-radok-camera-black-lw245bg408.jpeg")
    const [users, setUsers] = useState([])
    const loadUser = async () => {
        const result = await getAllUserAccount()
        setUser(result)
    }
    useEffect(() => {
        loadUser
    }, [])
    const handleChange = (e) => {
        setPreviewImage(URL.createObjectURL(e.target.files[0]))
    }
    return (
        <div className='container'>
            <h1 className='text-secondary mb-3 text-uppercase'>New Product</h1>
            <div className='text-center'>
                <div className='row'>
                    <div className='col 6'>
                        <div class="form-floating mb-3 mt-3">
                            <input type="text" class="form-control" id="code" style={{ width: '200px' }} />
                            <label for="code">Code</label>
                        </div>
                        <div class="form-floating mb-3 mt-3">
                            <input type="text" class="form-control" id="name" />
                            <label for="name">Product name</label>
                        </div>
                        <div class="form-floating mb-3 mt-3">
                            <select class="form-select" aria-label="Default select example">
                                <option selected disabled>User [Demo]</option>
                                {

                                }
                                <option value="1">One</option>
                                <option value="2">Two</option>
                                <option value="3">Three</option>
                            </select>
                        </div>
                    </div>
                    <div className='col-6'>
                        <img src={previewImage}
                            style={{ width: '50%' }} class="img-thumbnail" alt="..." />
                        <p className='mt-2'> <input type='file' name='image' className='btn' onChange={handleChange} /></p>
                    </div>
                </div>
            </div>
        </div>
    )
}
