import React, { useState } from 'react'

export default function Upload_Multiple() {
    const [images, setImages] = useState([])
    const handleChange = (e) => {
        const newImages = [...images];
        for (let i = 0; i < e.target.files.length; i++) {
            newImages.push(URL.createObjectURL(e.target.files[i]));
        }
        setImages(newImages);
    }

    const handleRemove = (index) => {
        const target = document.getElementById('image').files
        
        const listImages = [...images]
        listImages.splice(index, 1)
        setImages(listImages)
        console.log(target);
    }
    return (
        <div>
            Upload_Multiple
            <input type='file' multiple id='image' name='image' onChange={handleChange} />
            {
                images.map((item, index) => (
                    <div key={index}>
                        <button type="button" className="btn-close" onClick={() => handleRemove({ index })}></button>
                        <img className='m-3' src={item} />
                    </div>
                ))
            }
        </div>
    )
}
